mapper.select:
select * from ( select board.*, sysdate - to_date(to_char(wdate, 'yyyy-mm-dd')) newImg, row_number() over(order by refer desc, sunbun asc) rn from spring_board
)
where rn > #{start} and rn < #{end} and cg_num in(0,1)

select * from ( select board.*, sysdate - to_date(to_char(wdate, 'yyyy-mm-dd')) newImg, row_number() over(order by refer desc, sunbun asc) rn from spring_board
)
where rn > #{start} and rn < #{end} and cg_num = 2

select * from ( select board.*, sysdate - to_date(to_char(bdate, 'yyyy-mm-dd')) newImg, row_number() over(order by refer desc, sunbun asc) rn from board
)
where rn > #{start} and rn < #{end}

select * from(
select a.*,row_number() over(order by bnum desc) rn from board a

)
where rn between #{start} and #{end}

mapper.update:
update board set btitle = #{btitle:VARCHAR},
bcontent = #{bcontent:VARCHAR},

    , filename =#{filename}, originFilename =#{originFilename}
    , filesize =#{filesize}

where idx=#{idx}
